import React, { useState, useEffect } from "react";
import "./Settings.css";

const Settings = () => {
  // Theme & Appearance
  const [theme, setTheme] = useState(localStorage.getItem("theme") || "light");
  const [fontSize, setFontSize] = useState("medium");

  // Units
  const [tempUnit, setTempUnit] = useState("C");
  const [windUnit, setWindUnit] = useState("km/h");

  // Notifications
  const [alertsEnabled, setAlertsEnabled] = useState(true);
  const [dailySummary, setDailySummary] = useState(false);

  // Location
  const [defaultCity, setDefaultCity] = useState("Dhaka");
  const [autoDetect, setAutoDetect] = useState(true);

  // Apply theme dynamically
  useEffect(() => {
    document.body.className = theme;
    localStorage.setItem("theme", theme);
  }, [theme]);

  return (
    <section className="settings-section">
      <h2 className="settings-title">Dashboard Settings</h2>

      {/* Theme & Appearance */}
      <div className="settings-card">
        <h3>Theme & Appearance</h3>
        <div className="settings-option">
          <label>Dark Mode:</label>
          <input
            type="checkbox"
            checked={theme === "dark"}
            onChange={() => setTheme(theme === "light" ? "dark" : "light")}
          />
        </div>
        <div className="settings-option">
          <label>Font Size:</label>
          <select value={fontSize} onChange={(e) => setFontSize(e.target.value)}>
            <option value="small">Small</option>
            <option value="medium">Medium</option>
            <option value="large">Large</option>
          </select>
        </div>
      </div>

      {/* Units */}
      <div className="settings-card">
        <h3>Units & Measurement</h3>
        <div className="settings-option">
          <label>Temperature:</label>
          <select value={tempUnit} onChange={(e) => setTempUnit(e.target.value)}>
            <option value="C">Celsius (°C)</option>
            <option value="F">Fahrenheit (°F)</option>
            <option value="K">Kelvin (K)</option>
          </select>
        </div>
        <div className="settings-option">
          <label>Wind Speed:</label>
          <select value={windUnit} onChange={(e) => setWindUnit(e.target.value)}>
            <option value="km/h">km/h</option>
            <option value="mph">mph</option>
            <option value="m/s">m/s</option>
          </select>
        </div>
      </div>

      {/* Notifications */}
      <div className="settings-card">
        <h3>Notifications</h3>
        <div className="settings-option">
          <label>Severe Weather Alerts:</label>
          <input
            type="checkbox"
            checked={alertsEnabled}
            onChange={() => setAlertsEnabled(!alertsEnabled)}
          />
        </div>
        <div className="settings-option">
          <label>Daily Summary:</label>
          <input
            type="checkbox"
            checked={dailySummary}
            onChange={() => setDailySummary(!dailySummary)}
          />
        </div>
      </div>

      {/* Location */}
      <div className="settings-card">
        <h3>Location</h3>
        <div className="settings-option">
          <label>Default City:</label>
          <input
            type="text"
            value={defaultCity}
            onChange={(e) => setDefaultCity(e.target.value)}
          />
        </div>
        <div className="settings-option">
          <label>Auto-Detect Location:</label>
          <input
            type="checkbox"
            checked={autoDetect}
            onChange={() => setAutoDetect(!autoDetect)}
          />
        </div>
      </div>
    </section>
  );
};

export default Settings;
