// src/Components/ProfileMenu/ProfileMenu.jsx
import React, { useState, useRef, useEffect } from "react";
import { User, ChevronDown, LogOut, LogIn } from "lucide-react";
import { useNavigate } from "react-router-dom";
import "./ProfileMenu.css";

const ProfileMenu = ({ user, onSignOut, onSignIn }) => {
  const [open, setOpen] = useState(false);
  const menuRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  const handleSignOutClick = () => {
    onSignOut();
    navigate("/signout");
    setOpen(false);
  };

  return (
    <div className="profile-menu" ref={menuRef}>
      <button className="profile-btn" onClick={() => setOpen(!open)}>
        <User size={18} /> {user ? user.name : "Guest"} <ChevronDown size={14} />
      </button>

      {open && (
        <div className="dropdown-menu">
          {user ? (
            <>
              <div className="dropdown-header">
                <User size={24} /> <span>Hi, {user.name}!</span>
              </div>
              <div className="dropdown-footer">
                <button onClick={handleSignOutClick} className="signout-btn">
                  <LogOut size={16} /> Sign Out
                </button>
              </div>
            </>
          ) : (
            <div className="dropdown-login">
              <p>You are not logged in.</p>
              <button onClick={onSignIn} className="login-btn">
                <LogIn size={16} /> Sign In
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ProfileMenu;
