import React, { Component } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Navbar from "./Components/Navbar/Navbar";
import Home from "./Components/Home/Home";
import About from "./Components/About/About";
import News from "./Components/News/News"; 
import Settings from "./Components/Settings/Settings"; 
import ProtectedRoute from "./";

export class App extends Component {
  state = {
    user: null, // logged out by default
  };

  setUser = (user) => this.setState({ user });

  render() {
    const { user } = this.state;

    return (
      <Router>
        <Navbar user={user} setUser={this.setUser} />
        <Routes>
          <Route path="/" element={<Home />} /> {/* public home page */}

          {/* Protected pages */}
          <Route path="/about" element={
            <ProtectedRoute user={user}><About /></ProtectedRoute>
          } />
          <Route path="/news" element={
            <ProtectedRoute user={user}><News /></ProtectedRoute>
          } />
          <Route path="/settings" element={
            <ProtectedRoute user={user}><Settings /></ProtectedRoute>
          } />
        </Routes>
      </Router>
    );
  }
}

export default App;
