// src/components/ProfileMenu/ProfileMenu.js
import React, { useState, useRef, useEffect } from "react";
import { User, ChevronDown, LogOut, Settings, LifeBuoy, Grid, LogIn } from "lucide-react";
import "./ProfileMenu.css";

const ProfileMenu = ({ user, onSignOut, onSignIn }) => {
  // user: { name: "John Doe" } if logged in, null if not
  const [open, setOpen] = useState(false);
  const menuRef = useRef(null);

  // Close dropdown on click outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  return (
    <div className="profile-menu" ref={menuRef}>
      <button className="profile-btn" onClick={() => setOpen(!open)}>
        <User size={18} /> {user ? user.name : "Guest"} <ChevronDown size={14} />
      </button>

      {open && (
        <div className="dropdown-menu">
          {user ? (
            <>
              <div className="dropdown-header">
                <User size={24} /> <span>Hi, {user.name}!</span>
              </div>
              <ul className="dropdown-links">
                <li><Grid size={16} /> <a href="/dashboard">Dashboard</a></li>
                <li><Settings size={16} /> <a href="/settings">Settings</a></li>
                <li><LifeBuoy size={16} /> <a href="/help">Help & Support</a></li>
              </ul>
              <div className="dropdown-footer">
                <button onClick={onSignOut} className="signout-btn">
                  <LogOut size={16} /> Sign Out
                </button>
              </div>
            </>
          ) : (
            <div className="dropdown-login">
              <p>You are not logged in.</p>
              <button className="login-btn" onClick={onSignIn}>
                <LogIn size={16} /> Sign In
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ProfileMenu;
