import React, { useState, useEffect } from "react";
import "./Dashboard.css";

const Dashboard = () => {
  const API_KEY = "97bfa96f153a4b679fc124940251110"; // âœ… à¦¤à§‹à¦®à¦¾à¦° WeatherAPI.com key

  const districts = [
    "Dhaka", "Chattogram", "Rajshahi", "Khulna", "Barishal", "Sylhet", "Rangpur", "Mymensingh",
    "Comilla", "Narayanganj", "Gazipur", "Jessore", "Bogra", "Pabna", "Kushtia", "Faridpur",
    "Tangail", "Narsingdi", "Noakhali", "Feni", "Brahmanbaria", "Cox's Bazar", "Natore", "Jamalpur",
    "Sherpur", "Netrokona", "Moulvibazar", "Habiganj", "Sunamganj", "Bagerhat", "Satkhira",
    "Jhenaidah", "Magura", "Narail", "Chuadanga", "Meherpur", "Pirojpur", "Patuakhali",
    "Barguna", "Bhola", "Jhalokathi", "Gaibandha", "Dinajpur", "Thakurgaon", "Nilphamari",
    "Panchagarh", "Kurigram", "Lalmonirhat", "Naogaon", "Joypurhat", "Chapainawabganj",
    "Sirajganj", "Kishoreganj", "Manikganj", "Munshiganj", "Shariatpur", "Madaripur",
    "Gopalganj", "Lakshmipur", "Bandarban", "Khagrachhari", "Rangamati"
  ];

  const [selectedDistrict, setSelectedDistrict] = useState("Dhaka");
  const [weather, setWeather] = useState(null);

  useEffect(() => {
    fetch(
      `https://api.weatherapi.com/v1/current.json?key=${API_KEY}&q=${selectedDistrict}&aqi=no`
    )
      .then((res) => res.json())
      .then((data) => setWeather(data))
      .catch((err) => console.error("Error fetching weather:", err));
  }, [selectedDistrict]);

  return (
    <div className="dashboard">
      <h2 className="dashboard__title">ðŸŒ¤ Live Weather Dashboard</h2>

      {/* District Dropdown */}
      <div className="district-select">
        <label htmlFor="district">Select District:</label>
        <select
          id="district"
          value={selectedDistrict}
          onChange={(e) => setSelectedDistrict(e.target.value)}
        >
          {districts.map((district) => (
            <option key={district} value={district}>
              {district}
            </option>
          ))}
        </select>
      </div>

      {/* Weather Info */}
      {weather ? (
        <div className="weather-card">
          <h3>{weather.location.name}, Bangladesh</h3>
          <div className="weather-main">
            <img src={weather.current.condition.icon} alt="weather icon" />
            <h2>{weather.current.temp_c}Â°C</h2>
          </div>
          <p>{weather.current.condition.text}</p>
          <p>Feels like: {weather.current.feelslike_c}Â°C</p>
          <p>Humidity: {weather.current.humidity}% | Wind: {weather.current.wind_kph} km/h</p>
        </div>
      ) : (
        <p>Loading weather data...</p>
      )}
    </div>
  );
};

export default Dashboard;
