// src/Components/ProfileMenu/ProfileMenu.jsx
import React, { useState, useRef, useEffect } from "react";
import { User, ChevronDown, LogOut, LogIn, Settings, Grid, LifeBuoy } from "lucide-react";
import { useNavigate } from "react-router-dom";
import "./ProfileMenu.css";

const ProfileMenu = ({ user, setUser }) => {
  const [open, setOpen] = useState(false);
  const menuRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) setOpen(false);
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Dynamic sign in (prompt user for name)
 const handleSignIn = () => {
  const name = prompt("Enter your name to log in:");
  if (name && name.trim() !== "") {
    setUser({ name: name.trim() }); // global user
  }
};


  // Sign out
  const handleSignOut = () => {
    setUser(null);
    navigate("/");
  };

  return (
    <div className="profile-menu" ref={menuRef}>
      <button className="profile-btn" onClick={() => setOpen(!open)}>
        <User size={18} /> {user ? user.name : "Guest"} <ChevronDown size={14} />
      </button>

      {open && (
        <div className="dropdown-menu">
          {user ? (
            <>
              <div className="dropdown-header">
                <User size={24} /> <span>Hi, {user.name}!</span>
              </div>
              <ul className="dropdown-links">
                <li><Grid size={16} /> <a href="/">Dashboard</a></li>
                <li><Settings size={16} /> <a href="/settings">Settings</a></li>
                <li><LifeBuoy size={16} /> <a href="/help">Help & Support</a></li>
              </ul>
              <div className="dropdown-footer">
                <button className="signout-btn" onClick={handleSignOut}>
                  <LogOut size={16} /> Sign Out
                </button>
              </div>
            </>
          ) : (
            <div className="dropdown-login">
              <p>You are not logged in.</p>
              <button className="login-btn" onClick={handleSignIn}>
                <LogIn size={16} /> Sign In
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ProfileMenu;
