import React, { useState, useEffect } from "react";
import "./News.css";
import { AlertTriangle, CloudRain, Sun, Wind } from "lucide-react";

const News = () => {
  const [newsList, setNewsList] = useState([]);

  // Simulated fetching news
  useEffect(() => {
    const fetchNews = async () => {
      const data = [
        {
          title: "Severe Thunderstorm Warning in Dhaka",
          description:
            "Heavy rains and thunderstorms expected today. Stay indoors and avoid travel if possible.",
          type: "alert",
          date: "2025-10-11",
        },
        {
          title: "Sunny Weather in Chittagong",
          description: "Clear skies and sunny conditions expected throughout the day.",
          type: "sunny",
          date: "2025-10-11",
        },
        {
          title: "Windy Conditions in Sylhet",
          description: "Expect strong gusts of wind up to 40 km/h in Sylhet region.",
          type: "wind",
          date: "2025-10-11",
        },
        {
          title: "Rain Alert in Barishal",
          description: "Light to moderate rain expected, carry umbrellas if going outside.",
          type: "rain",
          date: "2025-10-11",
        },
      ];
      setNewsList(data);
    };

    fetchNews();
  }, []);

  const getIcon = (type) => {
    switch (type) {
      case "alert":
        return <AlertTriangle size={28} color="#ff4d4f" />;
      case "rain":
        return <CloudRain size={28} color="#1890ff" />;
      case "sunny":
        return <Sun size={28} color="#fadb14" />;
      case "wind":
        return <Wind size={28} color="#73d13d" />;
      default:
        return <Sun size={28} />;
    }
  };

  const getBadge = (type) => {
    switch (type) {
      case "alert":
        return "Alert";
      case "rain":
        return "Rain";
      case "sunny":
        return "Sunny";
      case "wind":
        return "Windy";
      default:
        return "Info";
    }
  };

  return (
    <section className="news-section">
      <h2 className="news-title">Weather News & Alerts</h2>

      {newsList.length === 0 ? (
        <p className="news-empty">No news available at the moment.</p>
      ) : (
        <div className="news-grid">
          {newsList.map((news, index) => (
            <div key={index} className="news-card">
              <div className="news-icon">{getIcon(news.type)}</div>
              <div className="news-content">
                <span className={`news-badge ${news.type}`}>{getBadge(news.type)}</span>
                <h3 className="news-heading">{news.title}</h3>
                <p className="news-desc">{news.description}</p>
                <p className="news-date">{new Date(news.date).toDateString()}</p>
                <a href="#" className="news-readmore">Read More â†’</a>
              </div>
            </div>
          ))}
        </div>
      )}
    </section>
  );
};

export default News;
