import React, { useState, useRef, useEffect } from "react";
import {
  User,
  ChevronDown,
  LogOut,
  LogIn,
  Settings,
  Grid,
  LifeBuoy,
  Home,
} from "lucide-react";
import { useNavigate } from "react-router-dom";
import "./ProfileMenu.css";

const ProfileMenu = ({ user, setUser }) => {
  const [open, setOpen] = useState(false);
  const menuRef = useRef(null);
  const navigate = useNavigate();

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuRef.current && !menuRef.current.contains(e.target)) {
        setOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Sign in prompt
  const handleSignIn = () => {
    const name = prompt("Enter your name to log in:");
    if (name && name.trim() !== "") {
      setUser({ name: name.trim() });
      navigate("/dashboard"); // login হলে ড্যাশবোর্ডে নেবে
    }
  };

  // Sign out
  const handleSignOut = () => {
    setUser(null);
    navigate("/");
  };

  // Navigation handlers
  const goToHome = () => {
    navigate("/");
    setOpen(false);
  };

  const goToDashboard = () => {
    navigate("/dashboard");
    setOpen(false);
  };

  const goToSettings = () => {
    navigate("/settings");
    setOpen(false);
  };

  const goToHelp = () => {
    navigate("/help");
    setOpen(false);
  };

  return (
    <div className="profile-menu" ref={menuRef}>
      <button className="profile-btn" onClick={() => setOpen(!open)}>
        <User size={18} /> {user ? user.name : "Guest"} <ChevronDown size={14} />
      </button>

      {open && (
        <div className="dropdown-menu">
          {user ? (
            <>
              <div className="dropdown-header">
                <User size={22} /> <span>Hi, {user.name}!</span>
              </div>

              <ul className="dropdown-links">
                <li onClick={goToDashboard}>
                  <Home size={16} /> Dashboard
                </li>
                {/* <li onClick={goToDashboard}>
                  <Grid size={16} /> Dashboard
                </li> */}
                <li onClick={goToSettings}>
                  <Settings size={16} /> Settings
                </li>
                <li onClick={goToHelp}>
                  <LifeBuoy size={16} /> Help & Support
                </li>
              </ul>

              <div className="dropdown-footer">
                <button className="signout-btn" onClick={handleSignOut}>
                  <LogOut size={16} /> Sign Out
                </button>
              </div>
            </>
          ) : (
            <div className="dropdown-login">
              <p>You are not logged in.</p>
              <button className="login-btn" onClick={handleSignIn}>
                <LogIn size={16} /> Sign In
              </button>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default ProfileMenu;
