import React, { useState, useEffect, useRef } from "react";
import { Sun, Moon, Menu, X, Bell, User, ChevronDown } from "lucide-react";
import "./Navbar.css";

const Navbar = () => {
  const [theme, setTheme] = useState("light");
  const [menuOpen, setMenuOpen] = useState(false);
  const [profileOpen, setProfileOpen] = useState(false);
  const profileRef = useRef(null);

  // Load theme from localStorage
  useEffect(() => {
    const savedTheme = localStorage.getItem("theme") || "light";
    setTheme(savedTheme);
    document.body.className = savedTheme;
  }, []);

  // Click outside profile to close dropdown
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (profileRef.current && !profileRef.current.contains(e.target)) {
        setProfileOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Toggle dark/light theme
  const toggleTheme = () => {
    const nextTheme = theme === "light" ? "dark" : "light";
    setTheme(nextTheme);
    document.body.className = nextTheme;
    localStorage.setItem("theme", nextTheme);
  };

  // Handle notification click
  const handleNotification = () => {
    alert("You have no new notifications!"); // Simple demo; replace with your logic
  };

  return (
    <header className={`navbar ${theme}`}>
      <div className="navbar__container">
        {/* Logo and links */}
        <div className="navbar__left">
          <div className="logo">üå§Ô∏è WeatherMate</div>
          <ul className="navbar__links">
            <li><a href="#home">Home</a></li>
            <li><a href="#features">Features</a></li>
            <li><a href="#a ">About</a></li>
          </ul>
        </div>

        {/* Right icons */}
        <div className="navbar__right">
          <button className="icon-btn" onClick={handleNotification}>
            <Bell size={18} />
          </button>

          <button className="icon-btn" onClick={toggleTheme}>
            {theme === "light" ? <Moon size={18} /> : <Sun size={18} />}
          </button>

          <div className="profile" ref={profileRef}>
            <button
              className="profile__btn"
              onClick={() => setProfileOpen(!profileOpen)}
            >
              <User size={18} /> <ChevronDown size={14} />
            </button>
            {profileOpen && (
              <div className="profile__menu">
                <a href="#">Profile</a>
                <a href="#">Settings</a>
                <a href="#">Sign out</a>
              </div>
            )}
          </div>

          <button className="menu-btn" onClick={() => setMenuOpen(!menuOpen)}>
            {menuOpen ? <X size={20} /> : <Menu size={20} />}
          </button>
        </div>
      </div>

      {/* Mobile menu */}
      {menuOpen && (
        <div className={`mobile-menu ${theme}`}>
          <a href="#home" onClick={() => setMenuOpen(false)}>Home</a>
          <a href="#features" onClick={() => setMenuOpen(false)}>Features</a>
          <a href="#blog" onClick={() => setMenuOpen(false)}>About</a>
        </div>
      )}
    </header>
  );
};

export default Navbar;
