import React, { useState, useEffect, useRef } from "react";
import { Sun, Moon, Menu, X, Search, Bell, User, Globe, ChevronDown } from "lucide-react";
import "./Navbar.css";

const Navbar = () => {
  const [theme, setTheme] = useState("light");
  const [menuOpen, setMenuOpen] = useState(false);
  const [profileOpen, setProfileOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const [debouncedTerm, setDebouncedTerm] = useState("");
  const [recentSearches, setRecentSearches] = useState([]);
  const [showDropdown, setShowDropdown] = useState(false);
  const profileRef = useRef(null);
  const searchRef = useRef(null);

  // Load recent searches from localStorage
  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("recentSearches") || "[]");
    setRecentSearches(stored);
  }, []);

  // Handle clicks outside profile or search
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (profileRef.current && !profileRef.current.contains(e.target)) {
        setProfileOpen(false);
      }
      if (searchRef.current && !searchRef.current.contains(e.target)) {
        setShowDropdown(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, []);

  // Theme toggle
  const toggleTheme = () => {
    const next = theme === "light" ? "dark" : "light";
    setTheme(next);
    document.body.className = next;
  };

  // Debounce search input
  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedTerm(searchTerm);
    }, 500); // 500ms debounce

    return () => clearTimeout(handler);
  }, [searchTerm]);

  // Trigger API call when debounced term changes
  useEffect(() => {
    if (!debouncedTerm) return;

    // Call backend API
    fetch(`/weather/${debouncedTerm}`)
      .then((res) => res.json())
      .then((data) => {
        console.log("Weather data:", data);
      })
      .catch((err) => console.error(err));

    // Save to recent searches
    setRecentSearches((prev) => {
      const updated = [debouncedTerm, ...prev.filter((s) => s !== debouncedTerm)].slice(0, 5); // max 5
      localStorage.setItem("recentSearches", JSON.stringify(updated));
      return updated;
    });
  }, [debouncedTerm]);

  return (
    <header className="navbar">
      <div className="navbar__container">
        {/* Logo */}
        <div className="navbar__left">
          <div className="logo">🌤️ ☁️ WeatherMate 🌦️☁️</div>
          <ul className="navbar__links">
            <li><a href="#">Home</a></li>
            <li><a href="#">Features</a></li>
            <li><a href="#">Pricing</a></li>
            <li><a href="#">Blog</a></li>
          </ul>
        </div>

        {/* Search */}
        <div className="navbar__search" ref={searchRef}>
          <Search size={18} />
          <input
            type="text"
            placeholder="Search city..."
            value={searchTerm}
            onChange={(e) => {
              setSearchTerm(e.target.value);
              setShowDropdown(true);
            }}
          />
          {/* Recent Searches Dropdown */}
          {showDropdown && recentSearches.length > 0 && (
            <div className="search-dropdown">
              {recentSearches.map((city, idx) => (
                <div
                  key={idx}
                  className="search-item"
                  onClick={() => {
                    setSearchTerm(city);
                    setShowDropdown(false);
                  }}
                >
                  {city}
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Right section */}
        <div className="navbar__right">
          <button className="icon-btn"><Bell size={18} /></button>
          <button className="icon-btn" onClick={toggleTheme}>
            {theme === "light" ? <Moon size={18} /> : <Sun size={18} />}
          </button>

          <div className="profile" ref={profileRef}>
            <button className="profile__btn" onClick={() => setProfileOpen(!profileOpen)}>
              <User size={18} /> <ChevronDown size={14} />
            </button>
            {profileOpen && (
              <div className="profile__menu">
                <a href="#">Profile</a>
                <a href="#">Settings</a>
                <a href="#">Sign out</a>
              </div>
            )}
          </div>

          <button className="menu-btn" onClick={() => setMenuOpen(!menuOpen)}>
            {menuOpen ? <X size={20} /> : <Menu size={20} />}
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      {menuOpen && (
        <div className="mobile-menu">
          <a href="#">Home</a>
          <a href="#">Features</a>
          <a href="#">Blog</a>
        </div>
      )}
    </header>
  );
};

export default Navbar;
