import React, { useState, useEffect } from "react";
import "./Home.css";
import Dashboard from "../Dashboard/Dashboard";
import { divisionsData } from "../Data/Divisions"; // ‡¶Ü‡¶≤‡¶æ‡¶¶‡¶æ ‡¶´‡¶æ‡¶á‡¶≤ ‡¶•‡ßá‡¶ï‡ßá ‡¶°‡ßá‡¶ü‡¶æ ‡¶á‡¶Æ‡ßç‡¶™‡ßã‡¶∞‡ßç‡¶ü

const Home = () => {
  const [selectedDivision, setSelectedDivision] = useState("Dhaka");
  const [selectedDistrict, setSelectedDistrict] = useState("Dhaka");
  const [selectedUpazila, setSelectedUpazila] = useState("Savar");
  const [searchCity, setSearchCity] = useState("Dhaka");
  const [inputCity, setInputCity] = useState("");

  const handleSearch = () => {
    if (inputCity.trim()) {
      setSearchCity(inputCity.trim());
      setInputCity("");
    } else {
      setSearchCity(`${selectedUpazila}, ${selectedDistrict}`);
    }
  };

  useEffect(() => {
    const firstDistrict = Object.keys(divisionsData[selectedDivision])[0];
    setSelectedDistrict(firstDistrict);
    setSelectedUpazila(divisionsData[selectedDivision][firstDistrict][0]);
  }, [selectedDivision]);

  return (
    <main id="home" className="home">
      <header className="home__header">
        <h1 className="home__title">
          Welcome to <span className="home__highlight">WeatherMate</span> üå¶Ô∏è‚òÅÔ∏è
        </h1>
        <p className="home__subtitle">
          Check live weather in your city, district, or upazila!
        </p>

        <div className="home__search">
          <input
            type="text"
            placeholder="Or enter city/upazila..."
            value={inputCity}
            onChange={(e) => setInputCity(e.target.value)}
            className="home__input"
            onKeyDown={(e) => e.key === "Enter" && handleSearch()}
          />

          <select
            value={selectedDivision}
            onChange={(e) => setSelectedDivision(e.target.value)}
            className="home__select"
          >
            {Object.keys(divisionsData).map((division) => (
              <option key={division} value={division}>
                {division}
              </option>
            ))}
          </select>

          <select
            value={selectedDistrict}
            onChange={(e) => {
              const district = e.target.value;
              setSelectedDistrict(district);
              setSelectedUpazila(divisionsData[selectedDivision][district][0]);
            }}
            className="home__select"
          >
            {Object.keys(divisionsData[selectedDivision]).map((district) => (
              <option key={district} value={district}>
                {district}
              </option>
            ))}
          </select>

          <select
            value={selectedUpazila}
            onChange={(e) => setSelectedUpazila(e.target.value)}
            className="home__select"
          >
            {divisionsData[selectedDivision][selectedDistrict].map((upazila) => (
              <option key={upazila} value={upazila}>
                {upazila}
              </option>
            ))}
          </select>

          <button onClick={handleSearch} className="home__button">
            Search
          </button>
        </div>
      </header>

      <section className="home__dashboard">
        <Dashboard city={searchCity} />
      </section>
    </main>
  );
};

export default Home;
