import React, { useState } from "react";
import "./Home.css";
import Dashboard from "../Dashboard/Dashboard";

const Home = () => {
  const [searchCity, setSearchCity] = useState("Dhaka"); // default city
  const [inputCity, setInputCity] = useState("");

  const handleSearch = () => {
    if (inputCity.trim()) {
      setSearchCity(inputCity.trim());
      setInputCity(""); // optional: clear input after search
    }
  };

  return (
    <main id="home" className="home">
      <header className="home__header">
        <h1 className="home__title">
          Welcome to <span className="home__highlight">WeatherMate</span> üå¶Ô∏è‚òÅÔ∏è
        </h1>
        <p className="home__subtitle">
          Check the weather of your city and stay prepared every day!
        </p>

        {/* Search Input */}
        <div className="home__search">
          <input
            type="text"
            placeholder="Search city..."
            value={inputCity}
            onChange={(e) => setInputCity(e.target.value)}
            className="home__input"
            onKeyDown={(e) => e.key === "Enter" && handleSearch()}
          />
          <button onClick={handleSearch} className="home__button">
            Search
          </button>
        </div>
      </header>

      {/* Dashboard */}
      <section className="home__dashboard">
        <Dashboard searchCity={searchCity} />
      </section>
    </main>
  );
};

export default Home;
